import React, { createContext, useContext, useMemo } from 'react';
import createIdGenerator from './createIdGenerator';

const context = createContext(createIdGenerator('autoid'));
const { Provider } = context;

export function useId(id) {
  const generator = useContext(context);

  return useMemo(() => {
    if (id) {
      return id;
    }

    return generator();
  }, [id, generator]);
}

export function IdProvider({ children, prefix = 'autoid' }) {
  const value = useMemo(() => createIdGenerator(prefix), [prefix]);

  return <Provider value={value}>{children}</Provider>;
}

src/createIdGenerator.js.content:
/**
 * An extremely simplified uuid function that is scoped with a speciifc prefix. This
 * will just always increment the total count at the end of the prefix.
 *
 * ```ts
 * const uuid = createIdGenerator("my-custom-prefix");
 *
 * uuid(); // my-custom-prefix-0
 * uuid(); // my-custom-prefix-1
 * uuid(); // my-custom-prefix-2
 * ```
 *
 * @param prefix The string prefix to use
 * @return a function to call to generate a "uuid"
 */
export default function createIdGenerator(prefix) {
  const generator = (function* idGenerator() {
    let index = 0;
    while (true) {
      yield `${prefix}-${index}`;
      index += 1;
    }
  })();

  return () => generator.next().value;
}

src/AnimatingAppBar.module.scss.content:
@import '~@react-md/utils/dist/mixins';
@import '~@react-md/theme/dist/mixins';
@import '~@react-md/elevation/dist/mixins';
@import '~@react-md/button/dist/mixins';
@import '~@react-md/app-bar/dist/functions';
@import '~@react-md/app-bar/dist/mixins';

.container {
  @include rmd-elevation(6);
  @include rmd-utils-block-centered;

  max-width: 20rem;
  position: relative;

  // when it's in the mobile full page view, reset some of the styles again
  :global .rmd-dialog--full-page :local & {
    height: 100%;
    margin: 0;
    max-width: none;
    position: static;

    .header {
      position: fixed;
    }

    .content {
      height: 100%;
    }
  }
}

.header {
  @include rmd-button-theme-update-var(color, $rmd-white-base);
  @include rmd-theme(color, $rmd-white-base);

  // 20rem
  $width: 20 * 16;
  // app-bar-height (3.5rem) + 96 for custom offset
  $height: (3.5 * 16) + 96;

  // sass-lint:disable next-line no-url-protocols
  background-image: url(#{'https://picsum.photos/#{$width}/#{$height}?image=811'});
  height: auto;
  padding-bottom: var(--offset);

  // this is for the demo only since I don't want to move to
  // a different page for this demo to have a fixed app bar.
  // you wouldn't normally need to do this
  position: absolute;
  z-index: 3;
}

.title {
  transform: translateY(var(--offset));
}

.content {
  @include rmd-utils-scroll;

  height: 35rem;
  padding-top: calc(#{rmd-app-bar-theme-var(height)} + var(--offset));
}
