import React, { useMemo, useCallback } from "react";
import cn from "classnames";
import {
  APP_BAR_OFFSET_DENSE_CLASSNAME,
  APP_BAR_OFFSET_PROMINENT_DENSE_CLASSNAME,
} from "@react-md/app-bar";
import { useAppSize, useToggle, PhoneOnly } from "@react-md/utils";

import ConditionalFullPageDialog from "../ConditionalFullPageDialog";

import { PhoneContext } from "./context";
import DefaultAppBar from "./DefaultAppBar";
import StatusBar from "./StatusBar";
import ClosePhone from "./ClosePhone";

import styles from "./Phone.module.scss";

const DEFAULT_APP_BAR = <DefaultAppBar />;

const Phone = ({
  id,
  title = "Example",
  children,
  appBar = DEFAULT_APP_BAR,
  className,
  contentClassName,
  contentStacked: stacked = false,
  prominent,
  disableAppBar = false,
  disableContent = false,
  disableFocusOnMount = false,
  onPhoneClose,
  statusBar = false,
}) => {
  const { isPhone } = useAppSize();
  const [visible, enable, disable] = useToggle(false);
  const closePhone = useCallback(() => {
    disable();
    if (onPhoneClose) {
      onPhoneClose();
    }
  }, [onPhoneClose, disable]);

  if (visible && !isPhone) {
    closePhone();
  }

  const value = useMemo(
    () => ({
      id,
      title,
      closePhone,
    }),
    [id, title, closePhone]
  );

  return (
    <PhoneContext.Provider value={value}>
      <ConditionalFullPageDialog
        id={id}
        disabled={!isPhone}
        enable={enable}
        disable={closePhone}
        visible={visible}
        disableAppBar={disableAppBar || statusBar}
        disableContent={disableContent}
        disableFocusOnMount={disableFocusOnMount}
      >
        <div
          id={`${id}-phone`}
          className={cn(
            styles.phone,
            styles.fabOffset,
            {
              [styles.emulated]: !isPhone,
            },
            className
          )}
        >
          {(statusBar && <StatusBar id={id} isPhone={isPhone} />) || appBar}
          <div
            id={`${id}-content`}
            className={cn(
              styles.content,
              {
                [styles.flexColumn]: stacked,
                [APP_BAR_OFFSET_DENSE_CLASSNAME]:
                  !statusBar && appBar && !isPhone,
                [APP_BAR_OFFSET_PROMINENT_DENSE_CLASSNAME]:
                  !statusBar && appBar && !isPhone && prominent,
              },
              contentClassName
            )}
          >
            {children}
            {statusBar && (
              <PhoneOnly>
                <ClosePhone floating />
              </PhoneOnly>
            )}
          </div>
        </div>
      </ConditionalFullPageDialog>
    </PhoneContext.Provider>
  );
};

export default Phone;

