import React, { FC } from "react";
import { BadgeContainer, Badge } from "@react-md/badge";
import { Button } from "@react-md/button";
import { NotificationsSVGIcon } from "@react-md/material-icons";
import { Text } from "@react-md/typography";
import { COPYRIGHT } from "./unicode";

import "./CustomizingBadges.scss";

const Demo: FC = () => (
  <>
    <BadgeContainer className="copyright-badge-container">
      {/* since the badge is presentational, don't add the `aria-describedby` value */}
      <Text>Some amazing product</Text>
      <Badge id="copyright-badge" theme="clear">
        {COPYRIGHT}
      </Badge>
    </BadgeContainer>
    {/* this is _basically_ the `BadgedButton` component except with an extra `<span>` */}
    <BadgeContainer className="custom-badged-button">
      <Button
        id="custom-badged-button"
        aria-describedby="custom-badged-button-badge"
        buttonType="icon"
      >
        <NotificationsSVGIcon />
      </Button>
      <Badge id="custom-badged-button-badge">8</Badge>
    </BadgeContainer>
  </>
);

export default Demo;

src/CustomizingBadges.scss.content:
@import '~@react-md/badge/dist/mixins';
@import '~@react-md/utils/dist/mixins';

.copyright-badge-container {
  @include rmd-badge-theme-update-var(font-size, rmd-typography-value(body-1, font-size));
  @include rmd-badge-theme-update-var(top, 0.5rem);
  @include rmd-badge-theme-update-var(right, -0.25rem);

  // add padding for the badge and auto-swap when direction changes
  @include rmd-utils-rtl-auto(padding-right, 1rem);

  margin: 1rem;
}

.custom-badged-button {
  // update the badge to partially outside of the button
  @include rmd-badge-theme-update-var(top, -0.5rem);
  @include rmd-badge-theme-update-var(right, -0.5rem);

  margin: 1rem;
}

