import React from "react";
import { Divider } from "@react-md/divider";
import {
  Form,
  TextField,
  Password,
  Fieldset,
  Radio,
  useChoice,
  FormThemeProvider,
} from "@react-md/form";

import styles from "./TextFieldTypes.module.scss";

const types = [
  "password",
  "number",
  "tel",
  "email",
  "date",
  "time",
  "datetime-local",
  "month",
  "week",
  "url",
];

const themes = ["underline", "filled", "outline"];

const Demo = () => {
  const [currentTheme, handleThemeChange] = useChoice("outline");

  return (
    <FormThemeProvider theme={currentTheme}>
      <Form className={styles.container}>
        <Fieldset legend="Text field theme">
          {themes.map((theme) => (
            <Radio
              id={`text-field-types-theme-${theme}`}
              key={theme}
              name="theme"
              onChange={handleThemeChange}
              value={theme}
              checked={currentTheme === theme}
              label={theme}
            />
          ))}
        </Fieldset>
        {types.map((type) => (
          <TextField
            id={`text-field-type-${type}`}
            key={type}
            type={type}
            placeholder="Placeholder"
            label={type}
          />
        ))}

        <Divider />
        <Password
          id="example-password-field"
          label="Password"
          placeholder="Super secret password"
        />
      </Form>
    </FormThemeProvider>
  );
};

export default Demo;

