import React, { FC, Fragment } from "react";
import { ArrowDropDownSVGIcon } from "@react-md/material-icons";
import { DropdownMenu } from "@react-md/menu";
import { Text } from "@react-md/typography";
import { bem, HorizontalPosition, VerticalPosition } from "@react-md/utils";



import "./MenuPositioning.scss";

const items = ["Item 1", "Item 2", "Item 3"];

const verticalPositions: VerticalPosition[] = [
  "above",
  "below",
  "center",
  "top",
  "bottom",
];
const horizontalPositions: HorizontalPosition[] = [
  "inner-left",
  "inner-right",
  "center",
  "left",
  "right",
];

const block = bem("example-menu-button");

const Demo: FC = () => (
  <Fragment>
    {horizontalPositions.map((x, i) => (
      <Fragment key={x}>
        <Text type="headline-6" margin={i === 0 ? "none" : "top"}>
          Horizontal Position: <code>{x}<code>
        </Text>
        <div className="menu-positioning-container">
          <div className="menu-positioning-group">
            {verticalPositions.map(y => (
              <DropdownMenu
                id={`position-${x}-${y}`}
                key={y}
                anchor={{ x, y }}
                items={items}
                menuLabel="Menu"
                themeType="outline"
                dropdownIcon={
                  <ArrowDropDownSVGIcon className={block("icon")} />
                }
                className={block()}
              >
                <span className={block("text")}>{y}</span>
              </DropdownMenu>
            ))}
          </div>
        </div>
      </Fragment>
    ))}
  </Fragment>
);

export default Demo;

src/MenuPositioning.scss.content:
@import '~@react-md/icon/dist/mixins';
@import '~@react-md/utils/dist/mixins';

.menu-positioning-container {
  display: flex;
  justify-content: center;
}

.menu-positioning-group {
  align-items: center;
  display: flex;
  flex-direction: column;
}

.example-menu-button {
  // make equal width buttons that are centered
  flex: 1 0 auto;
  margin: 1rem auto;
  width: 100%;

  &__text {
    // since the icon is aligned to the end with margin, the default icon spacing can't
    // be applied as it relies on margin as well. If you apply padding to an icon, the sizing
    // gets messed up so it's easier to apply it to the text element here. now we have the same
    // text spacing as before for the largest button.
    @include rmd-icon-text-spacing($property: padding-right);
  }

  &__icon {
    // align the icon to the end since equal width buttons look bad with centered text/icons
    @include rmd-utils-rtl-auto(margin-left, auto);
  }
}
