import createIdGenerator from "../createIdGenerator";

export const uuid = createIdGenerator("drive-content");

export const createFolder = (name, lastModified) => ({
  id: uuid(),
  type: "folder",
  owner: "me",
  name,
  lastModified: lastModified.toISOString(),
});

export const createMedia = (name, lastModified, size, type) => ({
  id: uuid(),
  name,
  size,
  type,
  owner: "me",
  lastModified: lastModified.toISOString(),
