@import '~@react-md/button/dist/mixins';
@import '~@react-md/icon/dist/mixins';
@import '~@react-md/progress/dist/mixins';
@import '~@react-md/theme/dist/mixins';
@import '~@react-md/utils/dist/mixins';

.async-button {
  @include rmd-progress-theme-update-var(circular-size, rmd-icon-theme-var(size));

  // just to make the demos look a bit nicer
  margin: 1rem;
  // this makes it so the linear progress doesn't expand past the button bounds
  overflow: hidden;

  &--loading {
    @include rmd-button-theme-update-var(color, rmd-theme-var(text-disabled-on-background));
    @include rmd-button-theme-update-var(outline, rmd-theme-var(text-disabled-on-background));
  }

  &__overlay {
    // you'll really want this to be the `background` value or whatever the current
    // background is instead. You'll probably also want to add additional styles for
    // maintaining the "containted" styles since the contained button turns into
    // an "unstyled" button when disabled
    @include rmd-theme(background-color, surface);
    @include rmd-utils-full-screen(absolute);

    align-items: center;
    border-radius: inherit;
    box-shadow: inherit;
    display: flex;
    justify-content: center;
    z-index: 1;

    &--linear {
      top: auto;
    }
  }
}

src/AsyncButton.tsx.content:
import React, { FC, ReactNode } from "react";
import { Button, ButtonProps } from "@react-md/button";
import { TextIconSpacing } from "@react-md/icon";
import { CloudDownloadSVGIcon } from "@react-md/material-icons";
import {
  CircularProgress,
  getProgressA11y,
  LinearProgress,
} from "@react-md/progress";
import { bem } from "@react-md/utils";

import "./AsyncButton.scss";

export interface AsyncButtonProps extends ButtonProps {
  id: string;
  loading?: boolean;
  asyncType:
    | "circular-overlay"
    | "linear-overlay"
    | "icon-before"
    | "icon-after";
}

type DefaultProps = Required<Pick<AsyncButtonProps, "loading">>;
type WithDefaultProps = AsyncButtonProps & DefaultProps;

const block = bem("async-button");

// this is used while the loading state is enabled to "disable" the button.
// If we disable the entire button, keyboard focus is lost which is not desired.
const noop = (): void => {};

const AsyncButton: FC<AsyncButtonProps> = (providedProps) => {
  const {
    id,
    loading,
    asyncType,
    onClick,
    ...props
  } = providedProps as WithDefaultProps;
  const { themeType } = props;

  const progressId = `${id}-progress`;
  const linear = asyncType === "linear-overlay";
  let children: ReactNode = null;
  switch (asyncType) {
    case "icon-before":
    case "icon-after":
      children = (
        <TextIconSpacing
          iconAfter={asyncType === "icon-after"}
          icon={
            loading ? (
              <CircularProgress id={progressId} centered={false} />
            ) : (
              <CloudDownloadSVGIcon />
            )
          }
        >
          Download
        </TextIconSpacing>
      );
      break;
    case "circular-overlay":
    case "linear-overlay":
      children = (
        <>
          <TextIconSpacing icon={<CloudDownloadSVGIcon />}>
            {loading ? "Loading..." : "Download"}
          </TextIconSpacing>
          {loading && (
            <span className={block("overlay", { linear })}>
              {linear ? (
                <LinearProgress id={progressId} />
              ) : (
                <CircularProgress id={progressId} />
              )}
            </span>
          )}
        </>
      );
    // no default
  }
  return (
    <Button
      {...props}
      id={id}
      className={block({ loading })}
      {...getProgressA11y(progressId, loading)}
      theme={loading ? "clear" : "primary"}
      onClick={loading ? noop : onClick}
      themeType={loading && themeType === "contained" ? "flat" : themeType}
    >
      {children}
    </Button>
  );
};

const defaultProps: DefaultProps = {
  loading: false,
};

AsyncButton.defaultProps = defaultProps;

export default AsyncButton;

