import React, { FC, HTMLAttributes } from "react";
import cn from "classnames";
import { bem } from "@react-md/utils";

import "./Container.scss";

interface ContainerProps extends HTMLAttributes<HTMLDivElement> {
  inline?: boolean;
}

const block = bem("progress-container");

const Container: FC<ContainerProps> = ({
  className,
  children,
  inline,
  ...props
}) => {
  return (
    <div {...props} className={cn(block({ inline }), className)}>
      {children}
    </div>
  );
};

export default Container;

src/useTemporaryToggle.ts.content:
import { useEffect } from "react";
import { useToggle } from "@react-md/utils";

const TEN_SECONDS = 10000;

interface ReturnValue {
  toggle: () => void;
  toggled: boolean;
  disable: () => void;
}

export default function useTemporaryToggle(
  duration: number = TEN_SECONDS
): ReturnValue {
  const [toggled, , disable, toggle] = useToggle(false);
  useEffect(() => {
    let timeout: number | undefined = window.setTimeout(() => {
      timeout = undefined;
      disable();
    }, duration);

    return () => window.clearTimeout(timeout);
  }, [toggled, disable, duration]);

  return { toggle, toggled, disable };
}
