import React, { FC, Suspense, useState } from "react";
import { AppBar } from "@react-md/app-bar";
import { CircularProgress } from "@react-md/progress";
import { Tabs, TabsManager } from "@react-md/tabs";
import { CrossFade } from "@react-md/transition";
import { Text } from "@react-md/typography";

import useFakeLazyImport from "./useFakeLazyImport";

import Container from "./Container";
import Page1 from "./Page1";
import Page2 from "./Page2";
import Page3 from "./Page3";

interface CurrentPageProps {
  page: number;
}

const CurrentPage: FC<CurrentPageProps> = ({ page }) => {
  let content = <Page1 />;
  if (page === 1) {
    content = <Page2 />;
  } else if (page === 2) {
    content = <Page3 />;
  }

  return <CrossFade>{content}</CrossFade>;
};

const CrossFadeExamplesAsync: FC = () => {
  // just make it so it doesn't start loading until a new tab is shown
  const [page, setPage] = useState<number>(-1);
  const Content = useFakeLazyImport<CurrentPageProps>(CurrentPage, page, 5000);

  return (
    <>
      <TabsManager
        activeIndex={page === -1 ? 0 : page}
        onActiveIndexChange={(index) => setPage(index)}
        tabs={["Page 1", "Page 2", "Page 3"]}
        tabsId="static-transition"
      >
        <AppBar theme="default">
          <Tabs />
        </AppBar>
      </TabsManager>
      <Container>
        {page === -1 && (
          <Text type="headline-6">
            {'Click on "Page 2" or "Page 3" to start the demo'}
          </Text>
        )}
        {page !== -1 && (
          <Suspense fallback={<CircularProgress id="async-loading-progress" />}>
            <Content page={page} />
          </Suspense>
        )}
      </Container>
    </>
  );
};

export default CrossFadeExamplesAsync;

src/CrossFadeExamplesStatic.tsx.content:
import React, { FC, useEffect, useRef, useState } from "react";
import { AppBar } from "@react-md/app-bar";
import { Tabs, TabsManager } from "@react-md/tabs";
import { CrossFade } from "@react-md/transition";

import Container from "./Container";
import Page1 from "./Page1";
import Page2 from "./Page2";
import Page3 from "./Page3";

const CrossFadeExamplesStatic: FC = () => {
  const [page, setPage] = useState<number>(0);
  let content = <Page1 />;
  if (page === 1) {
    content = <Page2 />;
  } else if (page === 2) {
    content = <Page3 />;
  }

  // don't want the first render to trigger the animation, but all changes afterwards
  // should.
  const rendered = useRef(false);
  useEffect(() => {
    rendered.current = true;
  }, []);

  return (
    <>
      <TabsManager
        activeIndex={page}
        onActiveIndexChange={(index) => setPage(index)}
        tabs={["Page 1", "Page 2", "Page 3"]}
        tabsId="static-transition"
      >
        <AppBar theme="default">
          <Tabs />
        </AppBar>
      </TabsManager>
      <Container>
        <CrossFade key={page} appear={rendered.current}>
          {content}
        </CrossFade>
      </Container>
    </>
  );
};

export default CrossFadeExamplesStatic;

