@import '~@react-md/elevation/dist/mixins';
@import '~@react-md/overlay/dist/mixins';
@import '~@react-md/theme/dist/mixins';
@import '~@react-md/transition/dist/mixins';

.form {
  display: flex;
  flex-wrap: wrap;

  fieldset {
    margin: 1rem;
  }
}

.select {
  margin: 1rem;
  width: 10rem;
}

.listbox {
  max-height: 25rem;
}

.footer {
  display: flex;
  justify-content: center;
  margin: 1rem;
  width: 100%;
}

.div {
  @include rmd-elevation(2);
  @include rmd-theme(background-color, surface);

  padding: 1rem;
  z-index: $rmd-overlay-z-index + 1;
}

.enter {
  transform: scale(0);
}

.entering {
  @include rmd-transition(deceleration);

  transform: scale(1);
  transition: transform 0.2s;
}

.exit {
  @include rmd-transition(acceleration);

  opacity: 1;
  transform: scale(1);
  transition: transform 0.15s, opacity 0.15s;
}

.exiting {
  opacity: 0;
  transform: scale(0);
}
