import React from "react";
import { render } from "react-dom";
import { Configuration } from "@react-md/layout";

// the styles are actually loaded as the pre-compiled themes in the index.html
// to speed up sandbox compilation. If you want to update the theme, you'll need
// to uncomment this line and remove the `<link>` tag in the `index.html`
// import "./styles.scss"

import Demo from "./Demo";

const App = () => (
  <Configuration>
    <Demo />
  </Configuration>
);

render(<App />, document.getElementById("root"));

src/MediaQueryComponents.module.scss.content:
@import "~@react-md/theme/dist/mixins";
@import "~@react-md/utils/dist/mixins";
@import "~@react-md/elevation/dist/mixins";

.container {
  @include rmd-utils-phone-media {
    // only applied **up** to the phone max width
    border: 1px solid rmd-theme-var(error);
  }

  @include rmd-utils-tablet-only-media {
    // only applied between the phone max width + 1 and the tablet max width
    border: 4px solid rmd-theme-var(warning);
  }

  @include rmd-utils-tablet-media {
    // everything tablet sized and up will gain an elevation of 4
    @include rmd-elevation(4);

    // now that we have more real estate, double the padding for tablets
    // and above
    padding: 2rem;
  }

  @include rmd-utils-desktop-media {
    // add a primary border ond desktops
    border: 2px solid rmd-theme-var(primary);
  }

  @include rmd-utils-large-desktop-media {
    // increase the box-shadow on large screens
    @include rmd-elevation(8);
  }

  // every size will gain a 1rem margin and padding
  margin: 1rem;
  padding: 1rem;
}
